SELECT RelacaoFretes.CodRelacaoFrete, RelacaoFretes.CodFornecedor, Fornecedores.RazaoSocial, Prot.NumProtocolo, RelacaoFretes.DataEntradaRelacao, RelacaoFretes.DataPrevistaPagamento,
RelacaoFretes.DataChegadaDocumentos, ComboBox_Financeiro.Descricao AS Situacao,
ComboBox_Financeiro1.Descricao AS IndDiferenca, ISNULL(RelacaoFretes.TotalRelacao, 0) + ISNULL(RelacaoFretes.OutrasDespesas, 0) - ISNULL(RelacaoFretes.Desconto, 0) AS TotalPago, 
CASE WHEN RelacaoFretes.CodRelacaoFrete IN (0) THEN 'X' ELSE NULL END AS Marcador,  Ultpas.Localizacao, UltPas.DataHoraPassagem FROM RelacaoFretes 
INNER JOIN ContasPagar ON RelacaoFretes.CodContasPagar = ContasPagar.CodContasPagar INNER JOIN Fornecedores ON Fornecedores.CodFornecedor = RelacaoFretes.CodFornecedor 
LEFT OUTER JOIN ConhecimentosRelacaoFretes ON RelacaoFretes.CodRelacaoFrete = ConhecimentosRelacaoFretes.CodRelacaoFrete 
LEFT OUTER JOIN ConhecimentosTransporte ON ConhecimentosTransporte.Sequencial = ConhecimentosRelacaoFretes.NumSeqConhecto 
LEFT OUTER JOIN ComboBox_Financeiro ON RelacaoFretes.CodSituacao = ComboBox_Financeiro.Codigo AND ComboBox_Financeiro.NomeComboBox = 'cboSituacaoRelacaoFrete' 
LEFT OUTER JOIN ComboBox_Financeiro ComboBox_Financeiro1 ON RelacaoFretes.CodIndicadorDiferenca = ComboBox_Financeiro1.Codigo AND ComboBox_Financeiro1.NomeComboBox = 'cboIndicadorDiferenca'
LEFT OUTER JOIN ValoresRelacaoFretes ON ValoresRelacaoFretes.CodRelacaoFrete = RelacaoFretes.CodRelacaoFrete 
LEFT OUTER JOIN ComboBox_Financeiro ComboBox_Financeiro2 ON RelacaoFretes.TipoPagtoRelacao = ComboBox_Financeiro2.Codigo AND ComboBox_Financeiro2.NomeComboBox = 'cboTipoPagtoRelacao'
LEFT OUTER JOIN ConhecimentosTransporte CTRC2 ON ValoresRelacaoFretes.CodComplemento = CTRC2.Sequencial 
LEFT OUTER JOIN TiposValores ON TiposValores.CodTipoValor = ValoresRelacaoFretes.CodTipoValor
LEFT OUTER JOIN UltimoRegPassagem UltPas ON UltPas.CodRelacaoFrete = RelacaoFretes.CodRelacaoFrete 
LEFT OUTER JOIN Protocolo_RelacaoFretes Prot on Prot.CodRelacaoFrete = RelacaoFretes.CodRelacaoFrete AND Prot.NumProtocolo = (SELECT MAX(UltProt.NumProtocolo) 

FROM Protocolo_RelacaoFretes UltProt WHERE UltProt.CodRelacaoFrete = RelacaoFretes.CodRelacaoFrete)
WHERE RelacaoFretes.DataEntradaRelacao BETWEEN '01/01/2024 00:00:00' AND '09/19/2024 23:59:29' AND RelacaoFretes.CodFornecedor = 1082600 
GROUP BY RelacaoFretes.CodRelacaoFrete, RelacaoFretes.CodFornecedor, Fornecedores.RazaoSocial, Prot.NumProtocolo, RelacaoFretes.DataEntradaRelacao, RelacaoFretes.DataPrevistaPagamento, RelacaoFretes.DataChegadaDocumentos, ComboBox_Financeiro.Descricao, ComboBox_Financeiro1.Descricao, RelacaoFretes.TotalRelacao, RelacaoFretes.OutrasDespesas, RelacaoFretes.Desconto, UltPas.DataHoraPassagem, UltPas.Localizacao ORDER BY RelacaoFretes.CodRelacaoFrete


